package javaPrograms;

public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//while declaration of variable you have to use data type
		
		//Primitive Data type
		int num=5; 
		
		//Non-Primitive Data Type
		String name="Mohit";
		
		char letter='c';
		
		double dnum = 29.2;
		
		Boolean text = true;
		
		//+ operator separates the variable and string
		System.out.println(num + " " + name + " " + letter + " "+ dnum + " " + text);
			
	}

}
